import React, { useState, useRef } from "react";
import cn from "classnames";
import Button from './elements/Button';
import { DataStore, sectionFooterSecondaryContent, Auth } from 'aws-amplify';
import {Candidate, User, Caption} from '../models'

const INITIAL_HEIGHT = 46;

const CommentBox = (myPost) => {
    const containerRef = useRef(null);
    const [isExpanded, setIsExpanded] = useState(false);
    const [commentValue, setCommentValue] = useState("");

    const outerHeight = useRef(INITIAL_HEIGHT);
    const textRef = useRef(null);

    const onExpand = () => {
        if (!isExpanded) {
            outerHeight.current = containerRef.current.scrollHeight;
            setIsExpanded(true);
        }
    }

    const onChange = (e) => {
        setCommentValue(e.target.value);
	}

    const onClose = () => {
        setCommentValue("");
        outerHeight.current = INITIAL_HEIGHT;
        setIsExpanded(false);
    };

    const onSubmit = (e) => {
        let myUser = Auth.currentAuthenticatedUser({bypassCache: false})
        e.preventDefault();
        console.log(myPost)
        const original = DataStore.query(User, c => c.username===myUser.username);
        DataStore.save(
          new Caption({
            body: commentValue,
            likes: 0,
            Candidate: myPost,
            User: DataStore.save(User.copyOf(original, updated => {updated.username=myUser.username}))
          })
        );
        console.log(commentValue)

    }

    return (
        <form
        onSubmit={onSubmit}
        ref={containerRef}
        className={cn("comment-box", {
          expanded: isExpanded,
          collapsed: !isExpanded,
                modified: commentValue.length > 0,
        })}
        style={{
          minHeight: isExpanded ? outerHeight.current : INITIAL_HEIGHT
        }}
      >
        <div className="header">
        <div className="user">
      </div>
        </div>
    <textarea
      ref={textRef}
      onClick={onExpand}
      onFocus={onExpand}
      onChange={onChange}
      className="comment-field"
      placeholder="What are your thoughts?"
      value={commentValue}
      name="comment"
      id="comment"
    />
    <div className="actions">
      <Button type="button" className="cancel" tag="a" color="dark" wideMobile onClick={onClose}>
      Cancel
      </Button>
      <Button type="submit" disabled={commentValue.length < 1} tag="a" color="primary" onClick={onSubmit} wideMobile>
      Respond
      </Button>
    </div>
	    </form>
    );
};

export default CommentBox;
